sys_script_client.script:
function onLoad() {

	var location = g_form.getValue('u_string_7');
	if(location !==''){
		return ;
	}

	var city = g_scratchpad.city ;
	var country = g_scratchpad.country; 

	if(city && country){
		g_form.setValue('u_string_7',city+' '+country);
	}

	else if(city){
		g_form.setValue('u_string_7',city);
	}
	else if(country){
		g_form.setValue('u_string_7',country);
	}

	if(city || country){
		g_form.showFieldMsg('u_string_7','Value updated automatically - You may override by editing','info');
	}


}
